import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Header from "./components/Header";
import HomePage from "./pages/HomePage";
import DetailsPage from "./pages/DetailsPage";
import CartPage from "./pages/CartPage";
import Footer from "./components/Footer";

import Data from "./data/store-items.json";

class App extends React.Component {
  state = {
    items: Data,
  };
  order = this.state.map(product => 
    <CardItem 
        name={product.media}
        img={product.img}
        phar={product.phar}
        phone={product.phone}
        email={product.email}
        website={product.website}
    />
  );
  render() {
    return (
      <BrowserRouter>
        <Header />
          <Routes>
            <Route
              exact="true"
              path="/"
              element={
                <HomePage boxs={this.state.items} />
              }
            />
            <Route
              exact="true"
              path="/details/:id"
              element={
                <DetailsPage boxs={this.state.items} link={this.props.value} />
              }
            />
            <Route
              exact="true"
              path="/cart"
              element={
                <CartPage boxs={this.state.items} />
              }
            />
          </Routes>
        <Footer />
      </BrowserRouter>
    );
  }
}

export default App;
