    const totalNum = cart.map(ele => ele.num).reduce((acc, curr) =>  
      acc + curr, 0);
      console.log(totalNum)
      return totalNum;
  }
  const totalPrice = () => {
    const totalPrice = cart.map(ele => getPrice(ele.price) * ele.num).reduce((acc, curr) => 
