              handleClick={handleClick}
              num={num}
              setNum={setNum}
              totalNum={totalNum}
              totalPrice={totalPrice}
            // knowValue={knowValue}
            />
          }
        />
        <Route
          exact="true"
          path="/cart/"
          element={
          <CartPage 
            newBoxs={cart}
            handleChange={handleChange}
            totalNum={totalNum}
            totalPrice={totalPrice}
          />
          }
        />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
};

export default App;
