import React from "react";
import { getImg, getPrice } from "../utils/helper";

class DetailsHero extends React.Component {
    // state = {
    //     counter: 1
    // }

    // updateButton = () => {
    //     this.setState(prevState => {
    //         counter: prevState.counter +=1
    //     });
    // }
    render() {
        const productKey = this.props.link;
        const findKey = this.props.boxs.find((el) => el.image.includes(productKey));
        // const findKeyPrice = this.props.boxs.find((el) => el.price.includes(productKey));

        // const result = this.state.counter < 10 ? `0${this.state.counter}` : `${this.state.counter}`;
        return (
            <section className="details-hero">
                <div className="container">
                    <div className="imgs-details-hero">
                        <div className="boxs-imgs-details-hero">
                            <div className="boxs-imgs-single">{getImg(findKey.image)}</div>
                            <div className="boxs-imgs-single">{getImg(findKey.image)}</div>
                            <div className="boxs-imgs-single">{getImg(findKey.image)}</div>
                        </div>
                        <div className="big-imgs-details-hero">{getImg(findKey.image)}</div>
                    </div>
                    <div className="text-details-hero">
                        <div className="box-text-details-hero">
                            <span className="span-box-text-details-hero">
                                ${getPrice(findKey.price)}
                            </span>
                            <h2>{findKey.name}</h2>
                            <p>{findKey.description}</p>
                            <div className="colors-box-text-details-hero">
                                <div className="title-colors-box-text-details-hero">
                                    <span>color:</span>
                                    <span>pink</span>
                                </div>
                                <div className="boxs-colors-box-text-details-hero">
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                </div>
                            </div>
                            <div className="btn-box-text-details-hero">
                                {/* <input
                                    type={"text"}
                                    min={1}
                                    className="input-counter"
                                    placeholder="01"
                                    defaultValue={ this.props.num }
                                /> */}
                                <select
                                className="input-counter"
                                defaultValue={ this.props.num }
                                >
                                {(() => {
                                    let numbers = [];
                                    for(let i = 1; i < 20; i++){
                                        numbers.push(<option key={i} defaultValue={i}>{i}</option>)
                                    }
                                    return numbers;
                                })()}
                                </select>
                                {/* <button className="btn main-btn-add-bc" onClick={(e) => this.props.handleClick(findKey, e, productKey)}>
                                    <a href="#add to cart">add to cart</a>
                                </button> */}
                                <button
                                    className="btn main-btn-add-bc"
                                    onClick={(e) =>
                                        this.props.handleClick(e, findKey, productKey)
                                    }
                                >
                                    add to cart
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default DetailsHero;
