import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Header from "./components/Header";
import HomePage from "./pages/HomePage";
import DetailsPage from "./pages/DetailsPage";
import CartPage from "./pages/CartPage";
import Footer from "./components/Footer";

import Data from "./data/store-items.json";

const App = (props) => {
  const items = Data;
  const cart = [];

  const knowValue = (e) => {
    console.log(
      e.target.parentNode.parentNode.querySelector(".input-counter").value
    );
  };

  const handleClick = (item) => {
    const id = item.image.split("/")[2].split(".")[0];
    this.setState({
      cart: this.state.cart.concat(item),
    });
  };


    return (
      <BrowserRouter>
        <Header />
        <Routes>
          <Route
            exact="true"
            path="/"
            element={<HomePage boxs={items} />}
          />
          <Route
            exact="true"
            path="/details/:id"
            element={
              <DetailsPage
                boxs={items}
                link={props.value}
                handleClick={handleClick}
                knowValue={knowValue}
              />
            }
          />
          <Route
            exact="true"
            path="/cart"
            element={<CartPage newBoxs={cart} />}
          />
        </Routes>
        <Footer />
      </BrowserRouter>
    );

}

export default App;
