import React from "react";
import { getImg, getPrice } from "../utils/helper";

export const CartListProduct = (props) => {

    return (
        <div className="box-light-cart-list">
            {getImg(props.newBoxBaby.image)}
            <span>{props.newBoxBaby.name}</span>
            <input
                type={"text"}
                min={1}
                className="input-counter-cart-list"
                defaultValue={props.newBoxBaby.num}
                onChange={(eNum, ePrice) => {props.handleChange(e, props.newBoxBaby.price); props.totalNum(eNum); props.totalPrice(ePrice);}}
            />
            <span>
                {/* { props.newBoxBaby.num } * { props.newBoxBaby.price } */}
                {/* ${Math.round(props.newBoxBaby.num * getPrice(props.newBoxBaby.price))} */}
                ${(props.newBoxBaby.num * getPrice(props.newBoxBaby.price)).toFixed(2)}
            </span>
            <button className="btn main-btn-remove">remove</button>
        </div>
    );
};
