    //   return "no"
    // })
  }


  return (
    <BrowserRouter>
      <Header />
      <Routes>
        <Route exact="true" path="/" element={<HomePage boxs={items} />} />
        <Route
          exact="true"
          path="/details/:id"
          element={
            <DetailsPage
              boxs={items}
              link={props.value}
              handleClick={handleClick}
            // knowValue={knowValue}
            />
          }
        />
        <Route
          exact="true"
          path="/cart/"
          element={
          <CartPage 
            newBoxs={cart}
          />
          }
        />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
};

export default App;
