  return (
    <BrowserRouter>
      <Header />
        <Routes>
          <Route
            exact="true"
            path="/"
            element={
              <HomePage boxs={items} />
            }
          />
          <Route
            exact="true"
            path="/details/:id"
            element={
              <DetailsPage boxs={items} />
            }
          />
          <Route
            exact="true"
            path="/cart"
            element={
              <CartPage boxs={items} />
            }
          />
        </Routes>
      <Footer />
    </BrowserRouter>
  );

}

export default App;
