import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Header from "./components/Header";
import HomePage from "./pages/HomePage";
import DetailsPage from "./pages/DetailsPage";
import CartPage from "./pages/CartPage";
import Footer from "./components/Footer";
import {cutImgLink, getPrice} from "./utils/helper";

import Data from "./data/store-items.json";

const App = (props) => {
  const items = Data;
  const [cart, setCart] = useState([]);
  const [num, setNum] = useState(1);

  // const knowValue = (e) => {
  //   console.log(
  //     e.target.parentNode.parentNode.querySelector(".input-counter").value
  //   );
  // };

  // const handleClick = (item, e, link) => {
  //   e = e.target.parentNode.parentNode.querySelector(".input-counter").value;

  //   setCart([
  //     ...cart,
  //     {
  //       id: link,
  //       num: e,
  //       ...item,
  //     },
  //   ]);

    // cart.map((ite, ind) => {
    //   if(![...items][ind].image.split("/")[2].split(".")[0] === ite.id) {
    //     console.log("yse")
    //   }
    //   return "no"
    // })
  // }

  //==================================================

  const handleClick = (e, item, id) => {
    e = e.target.parentNode.parentNode.querySelector(".input-counter").value;

    let newItem = {...item, id, e};

    const newCart1 = cart.find((it => cutImgLink(it.image) === newItem.id));

    if(newCart1){
      const newCart2 = cart.map((it => cutImgLink(it.image) === newItem.id ? {...newCart1, num : newCart1.num + 1} : it));
      setCart(newCart2);
    } else {
      const newCart2 = [...cart, {...newItem, num: 1}];
      setCart(newCart2);
    }

    // const newArr = cart.find((it => it.name === item.name))
    // if(newArr){
    //   setCart(cart.map((it) => 
    //     it.name  === item.name ? {...newArr, quy: newArr.quy + 1} : item
    //   ))
    // } else {
    //   setCart([...cart, {...items, quy: 1}])
    // }
    // setCart([
    //   ...cart,
    //   {
    //     id: link,
    //     num: m,
    //     ...item,
    //   },
    // ]);

    // cart.map((ite, ind) => {
    //   if(![...items][ind].image.split("/")[2].split(".")[0] === ite.id) {
    //     console.log("yse")
    //   }
    //   return "no"
    // })
  }

  const handleChange = (value) => {
    const valueInput = value.target.value;
    let valueTotal = value.target.parentNode.querySelectorAll("span")[1];
    const valueResult = getPrice(cart.price);

    valueTotal.textContent = valueResult * valueInput;

    console.log(valueResult);
    // console.log(+valueTotal.split("$")[1] * valueInput);
  }

  return (
    <BrowserRouter>
      <Header />
      <Routes>
        <Route exact="true" path="/" element={<HomePage boxs={items} />} />
        <Route
          exact="true"
          path="/details/:id"
          element={
            <DetailsPage
              boxs={items}
              link={props.value}
              handleClick={handleClick}
              num={num}
              setNum={setNum}
            // knowValue={knowValue}
            />
          }
        />
        <Route
          exact="true"
          path="/cart/"
          element={
          <CartPage 
            newBoxs={cart}
            handleChange={handleChange}
          />
          }
        />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
};

export default App;
