import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Header from "./components/Header";
import HomePage from "./pages/HomePage";
import DetailsPage from "./pages/DetailsPage";
import CartPage from "./pages/CartPage";
import Footer from "./components/Footer";

import Data from "./data/store-items.json";

const App = (props) => {
  const items = Data;
  const [cart, setCart] = useState([]);

  // const knowValue = (e) => {
  //   console.log(
  //     e.target.parentNode.parentNode.querySelector(".input-counter").value
  //   );
  // };

  const handleClick = (item, e, link) => {
    e = e.target.parentNode.parentNode.querySelector(".input-counter").value;

    // if (!cart.includes(item.image.split("/")[2].split(".")[0])) return;

    // cart.map(cartBaby => {
    //   return cartBaby.includes(item.image.split("/")[2].split(".")[0]) ? cartBaby : "no"
    // })

    const filterCart = cart.find(el => el.id === item.image.split("/")[2].split(".")[0]);

    if (filterCart) {
      const newAddCart = cart.map((el) => { 
        el.id === item.image.split("/")[2].split(".")[0] ? [...filterCart, {
          id: link,
          num: e,
          ...item,
        }] : el
      })
      setCart(newAddCart);
    }

  };

  const onChangeValue = (item, value) => {
    const ind = cart.indexOf(item);
    const arr = cart;
    arr[ind].num += value;

    if (arr[ind].num === "") {
      return arr[ind].num === "1"
    }
    setCart([...arr]);
  };

  return (
    <BrowserRouter>
      <Header />
      <Routes>
        <Route exact="true" path="/" element={<HomePage boxs={items} />} />
        <Route
          exact="true"
          path="/details/:id"
          element={
            <DetailsPage
              boxs={items}
              link={props.value}
              handleClick={handleClick}
            // knowValue={knowValue}
            />
          }
        />
        <Route
          exact="true"
          path="/cart"
          element={
          <CartPage 
            newBoxs={cart} 
            setCart={setCart}
            onChangeValue={onChangeValue} 
          />
          }
        />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
};

export default App;
