import React from 'react';
import {getImg} from "../utils/helper";
import { Btn } from './Button';

class CartList extends React.Component {
    render() {
        // console.log(this.props.newBoxs[0].name);
        const productKey = this.props.link;
        const findKey = this.props.newBoxs.find(el => el.image.includes(productKey));
        return (
            <section className='cart-list'>
                <div className='container'>
                    <h5>
                        your cart
                    </h5>
                    <div className='light-cart-list'>
                        <div className='box-light-cart-list'>
                            { getImg(findKey.image) } 
                            <span>
                                { this.props.newBoxs[0].name }
                            </span>
                            <input type={"text"} className="input-counter-cart-list" />
                            <span>
                                { this.props.newBoxs[0].price }
                            </span>
                            <button className="btn main-btn-remove">remove</button>
                        </div>
                    </div>
                    <div className='dark-cart-list'>
                        <div className='box-dark-cart-list'>
                            <span>
                                all items
                            </span>
                            <div></div>
                            <span>
                                06
                            </span>
                            <span>
                                $320
                            </span>
                            <Btn classbtn="main-btn-dark-bc" text="check out all"/>
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default CartList;
