import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Header from "./components/Header";
import HomePage from "./pages/HomePage";
import DetailsPage from "./pages/DetailsPage";
import CartPage from "./pages/CartPage";
import Footer from "./components/Footer";

import Data from "./data/store-items.json";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: Data,
      show: [],
      cart: [],
    }
  }

    handleClick = (item) => {
        this.cart.push(item);
        console.log(this.cart);
    };

  render() {
    return (
      <BrowserRouter>
        <Header setShow={this.setShow}/>
          <Routes>
            <Route
              exact="true"
              path="/"
              element={
                <HomePage boxs={this.state.items} />
              }
            />
            <Route
              exact="true"
              path="/details/:id"
              element={
                <DetailsPage boxs={this.state.items} link={this.props.value} />
              }
            />
            <Route
              exact="true"
              path="/cart"
              element={
                <CartPage boxs={this.state.show} boxs={this.state.show} />
              }
            />
          </Routes>
        <Footer />
      </BrowserRouter>
    );
  }
}

export default App;
