import React from 'react';

export const Product = props => {
     const getImg = (img) => {
        return  <img src={require(`../img/${img}.png`)} alt={`there is img of ${props.item.name}`}/>
    }
    clg
    const cutImgLink = allname => allname.split("/")[2].split(".")[0];
    return (
        <div className='card-showitem'>
            <a href={`/details/${ cutImgLink(props.box.image) }`}>
                <div className='card-img-showitem'>
                    <img src={ props.box.image } alt={ props.box.name }/>
                </div>
                <div className='card-text-showitem'>
                    <span className='product name'>
                        { props.box.name }
                    </span>
                    <span className='product price'>
                        { props.box.price }
                    </span>
                </div>
            </a>
        </div>
    );
}


