import React from "react";
import { Btn } from "./Button";

import { ItemGrid } from "./ItemGrid";


export const ShowItem = (props) => {
    return (
        <section className="showitem">
            <ItemGrid
            products = {props}
                // { showProduct.slice(0, 3) }
            limit={-1}
            />
            <Btn classbtn="main-btn-light-bc" text="more products" />
        </section>
    );
};
