import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Header from "./components/Header";
import HomePage from "./pages/HomePage";
import DetailsPage from "./pages/DetailsPage";
import CartPage from "./pages/CartPage";
import Footer from "./components/Footer";
import {cutImgLink, getPrice} from "./utils/helper";

import Data from "./data/store-items.json";

const App = (props) => {
  const items = Data;
  const [cart, setCart] = useState([]);
  const [num, setNum] = useState(1);
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const [msg, setMsg] = useState("");

  // const knowValue = (e) => {
  //   console.log(
  //     e.target.parentNode.parentNode.querySelector(".input-counter").value
  //   );
  // };

  // const handleClick = (item, e, link) => {
  //   e = e.target.parentNode.parentNode.querySelector(".input-counter").value;

  //   setCart([
  //     ...cart,
  //     {
  //       id: link,
  //       num: e,
  //       ...item,
  //     },
  //   ]);

    // cart.map((ite, ind) => {
    //   if(![...items][ind].image.split("/")[2].split(".")[0] === ite.id) {
    //     console.log("yse")
    //   }
    //   return "no"
    // })
  // }

  //==================================================

  const handleClick = (e, item, id) => {
    e = e.target.parentNode.parentNode.querySelector(".select-counter").value;

    let newItem = {...item, id};

    const newCart1 = cart.find((it => cutImgLink(it.image) === newItem.id));

    if(newCart1){
      const newCart2 = cart.map((it => cutImgLink(it.image) === newItem.id ? {...newCart1, num : +e < 10 ? `0${+e}` : +e} : it));
      setCart(newCart2);
      localStorage.setItem("newCartItems", JSON.stringify(newCart2));
    } else {
      const newCart2 = [...cart, {...newItem, num: +e < 10 ? `0${+e}` : +e}];
      setCart(newCart2);
      localStorage.setItem("newCartItems", JSON.stringify(newCart2));
    }
    // const newArr = cart.find((it => it.name === item.name))
    // if(newArr){
    //   setCart(cart.map((it) => 
    //     it.name  === item.name ? {...newArr, quy: newArr.quy + 1} : item
    //   ))
    // } else {
    //   setCart([...cart, {...items, quy: 1}])
    // }
    // setCart([
    //   ...cart,
    //   {
    //     id: link,
    //     num: m,
    //     ...item,
    //   },
    // ]);

    // cart.map((ite, ind) => {
    //   if(![...items][ind].image.split("/")[2].split(".")[0] === ite.id) {
    //     console.log("yse")
    //   }
    //   return "no"
    // })
  }

  useEffect(() => { 
    setCart(
      localStorage.getItem("newCartItems") 
      ? JSON.parse(localStorage.getItem("newCartItems"))
      : []
    );
  }, []);

  const handleChange = (value, price) => {
    const valueInput = value.target.value;
    let valueTotal = value.target.parentNode.querySelectorAll("span")[1];
    let valueResult = getPrice(price) * valueInput;

    valueTotal.textContent = `$${valueResult.toFixed(2)}`;

    const getJs = JSON.parse(localStorage.getItem("newCartItems"));
    const myImg = value.target.parentNode.querySelector("img").src.split("/")[5].split(".")[0];
    const imJs1 = getJs.find(elem => myImg === cutImgLink(elem.image));

    if (imJs1) {
      const newGetJs = getJs.map((elem => myImg === cutImgLink(elem.image) ? {...imJs1, num : valueInput} : elem));
      setCart(newGetJs);
      localStorage.setItem("newCartItems", JSON.stringify(newGetJs));
    } else {
      const newGetJs = [...imJs1, {...getJs, num: valueInput}];
      setCart(newGetJs);
      localStorage.setItem("newCartItems", JSON.stringify(newGetJs));
    }

    // console.log(getJs.map(elem => elem.num = valueInput))
    // console.log(getJs.map(elem => getPrice(elem.price)* valueInput))

    // const setJs = localStorage.setItem("newCartItems", JSON.stringify(getJs));
    // console.log(getJs)
    // console.log(setJs)
  }

  const totalNum = () => {
    // const totalNum = cart.map(ele => +ele.num).reduce((acc, curr) =>  
    // acc + curr, 0);
    // return totalNum;
    const getJs = JSON.parse(localStorage.getItem("newCartItems"));
    const totalNum = getJs.map(elem => +elem.num).reduce((acc, curr) =>  
    acc + curr, 0);

    console.log(totalNum);
    return totalNum < 10 ? `0${totalNum}` : `${totalNum}`;
  }
  const totalPrice = () => {
    // const totalPrice = cart.map(ele => getPrice(ele.price) * ele.num).reduce((acc, curr) => 
    // acc + curr, 0);
    // return totalPrice.toFixed(2);
    const getJs = JSON.parse(localStorage.getItem("newCartItems"));
    const totalPrice = getJs.map(elem => getPrice(elem.price) * elem.num).reduce((acc, curr) => 
    acc + curr, 0);

    console.log(totalPrice.toFixed(2));
    return totalPrice.toFixed(2);
  }

  const handleRemove = (e) => {
    const myImg = e.id;

    const getJs = JSON.parse(localStorage.getItem("newCartItems"));
    const imJs1 = getJs.find(elem => myImg === elem.id);
    if (imJs1) {
      const newGetJs = getJs.filter((elem => myImg !== elem.id));
      setCart(newGetJs);
      localStorage.removeItem(newGetJs);
      localStorage.setItem("newCartItems", JSON.stringify(newGetJs));
      console.log(imJs1);
      console.log(newGetJs);
    }
  }

  const checkEmail = (e) => {
    setEmail(e.target.value);
    const regEmail = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    if (regEmail.test(email) === false) {
      setError(`Please enter valid email address`);
    } else {
      setError(``);
      return true;
    }
  }

  const onSubmit = () => {
    if (email != "") {
      setMsg(`Thank you for ${email}`);
    } else {
      setError
    }
  }
  return (
    <BrowserRouter>
      <Header />
      <Routes>
        <Route exact="true" path="/" element={<HomePage boxs={items} />} />
        <Route
          exact="true"
          path="/details/:id"
          element={
            <DetailsPage
              boxs={items}
              link={props.value}
              handleClick={handleClick}
              num={num}
              setNum={setNum}
              totalNum={totalNum}
              totalPrice={totalPrice}
              checkEmail={checkEmail}
              onSubmit={onSubmit}
            // knowValue={knowValue}
            />
          }
        />
        <Route
          exact="true"
          path="/cart/"
          element={
          <CartPage 
            newBoxs={cart}
            handleChange={handleChange}
            totalNum={totalNum}
            totalPrice={totalPrice}
            handleRemove={handleRemove}
            checkEmail={checkEmail}
            onSubmit={onSubmit}
          />
          }
        />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
};

export default App;
