import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Header from "./components/Header";
import HomePage from "./pages/HomePage";
import DetailsPage from "./pages/DetailsPage";
import CartPage from "./pages/CartPage";
import Footer from "./components/Footer";

import Data from "./data/store-items.json";

const App = (props) => {
  con

  knowValue = (e) => {
    console.log(
      e.target.parentNode.parentNode.querySelector(".input-counter").value
    );
  };

  handleClick = (item) => {
    const id = item.image.split("/")[2].split(".")[0];
    this.setState({
      cart: this.state.cart.concat(item),
    });

    console.log(this.state.cart);
    console.log(id);
  };


    return (
      <BrowserRouter>
        <Header />
        <Routes>
          <Route
            exact="true"
            path="/"
            element={<HomePage boxs={this.state.items} />}
          />
          <Route
            exact="true"
            path="/details/:id"
            element={
              <DetailsPage
                boxs={this.state.items}
                link={this.props.value}
                handleClick={this.handleClick}
                knowValue={this.knowValue}
              />
            }
          />
          <Route
            exact="true"
            path="/cart"
            element={<CartPage newBoxs={this.state.cart} />}
          />
        </Routes>
        <Footer />
      </BrowserRouter>
    );

}

export default App;
