export const ShowItem = (props) => {
    const showProduct = props.boxs.map(box => { 
        return <Product
            key={props}
            name={box.name}
            price={box.price}
            image={box.image}
        />
    });
    return (
        <section className="showitem">
            <ItemGrid>
                <Product
                    name={props.boxs[0].name}
                    price={props.boxs[0].price}
                    image={props.boxs[0].image}
                />
            </ItemGrid>
            <Btn classbtn="main-btn-light-bc" text="more products" />
        </section>
    );
};
